(ns adv.y2022)

(defn unique? [coll]
  (= (count (distinct coll))
     (count coll)))

(defn zip [c1 c2]
  (partition 2 2 (interleave c1 c2)))

(defn part1 [s]
  (let [ps (partition 4 1 s)
        with-index (zip (range 1 (count ps)) ps)
        first-uniq (first (filter (fn [[_ y]] (unique? y)) with-index))]
    (+ 3 (first first-uniq))))

(defn part2 [s]
  (let [ps (partition 14 1 s)
        with-index (zip (range 1 (count ps)) ps)
        first-uniq (first (filter (fn [[_ y]] (unique? y)) with-index))]
    (+ 13 (first first-uniq))))


